import React from "react";
import { useResumeDataContext } from "../Providers/ResumeDataProvider";

const Education = () => {
    const { resumeData } = useResumeDataContext();

    return (
        <div className="tab-contents active-tab bg-transparent">
            <div>
                {resumeData[0].education.map((education, index) => (
                    <div
                        key={index}
                        className="bg-white bg-opacity-30 p-4 rounded-lg mb-4"
                    >
                        <h3 className="text-xl font-semibold mb-2">{education.degree}</h3>
                        <p className="text-lg mb-2">{education.university}</p>
                        <p className="text-gray-500 mb-1">Completion Date: {education.completionDate}</p>
                        <p className="text-gray-500 mb-1">Grade: {education.grade}</p>
                    </div>
                ))}
                <div className="bg-white bg-opacity-30 p-4 rounded-lg">
                    <h3 className="text-xl font-semibold mb-2">Certifications:</h3>
                    <ul className="mb-2">
                        {Object.values(resumeData[0].certifications[0]).map(
                            (certification, index) => (
                                <li key={index} className="text-gray-500 mb-1">
                                    {certification[0].name} | Completion Date: {certification[0].completionDate}
                                </li>
                            )
                        )}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default Education;
