import React from "react";
import { useResumeDataContext } from "../Providers/ResumeDataProvider";
import { FaGraduationCap, FaCertificate } from "react-icons/fa";

const Education = () => {
  const { resumeData } = useResumeDataContext();

  return (
    <div className="tab-contents active-tab bg-gray-200">
      <div>
        {resumeData[0].education.map((education, index) => (
          <div
            key={index}
            className="bg-white bg-opacity-90 p-4 rounded-lg flex items-center"
          >
            <FaGraduationCap className="text-blue-500 text-3xl" />
            <div className="ml-4">
              <h3 className="text-xl font-semibold">{education.degree}</h3>
              <p className="text-lg mb-2">{education.university}</p>
              <p className="text-gray-600">
                Completion Date: {education.completionDate}
              </p>
              <p className="text-gray-600">Grade: {education.grade}</p>
            </div>
          </div>
        ))}
        <div className="bg-white bg-opacity-90 p-4 rounded-lg flex items-center">
          <FaCertificate className="text-blue-500 text-3xl" />
          <div className="">
            <h3 className="text-xl font-semibold">Certifications:</h3>
            <ul className="">
              {Object.values(resumeData[0].certifications[0]).map(
                (certification, index) => (
                  <li key={index} className="text-gray-600">
                    {certification[0].name} | Completion Date:{" "}
                    {certification[0].completionDate}
                  </li>
                )
              )}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Education;
