import React, { useEffect, useRef } from 'react';
import { FaPaintBrush, FaCode, FaMobileAlt } from 'react-icons/fa';

function Skills() {
    const listItemRefs = useRef([]);

    useEffect(() => {
        listItemRefs.current.forEach((item, index) => {
            item.style.animationDelay = `${index * 0.2}s`;
            item.classList.add('animate-fade-in');
        });
    }, []);

    return (
        <div className="tab-contents active-tab">
            <ul className="">
                <li
                    ref={(el) => (listItemRefs.current[0] = el)}
                    className="items-center mb-5 p-5 bg-[rgba(0,0,0,0.45)] border-black rounded-xl shadow-md animate-fade-in"
                >
                    {/* Rest of the code */}
                </li>
                <li
                    ref={(el) => (listItemRefs.current[1] = el)}
                    className="items-center mb-5 p-5  bg-[rgba(0,0,0,0.45)] backdrop-blur border-black rounded-xl shadow-lg animate-fade-in"
                >
                    {/* Rest of the code */}
                </li>
                <li
                    ref={(el) => (listItemRefs.current[2] = el)}
                    className="items-center mb-5 p-5  bg-[rgba(0,0,0,0.45)] backdrop-blur border-black rounded-xl shadow-lg animate-fade-in"
                >
                    {/* Rest of the code */}
                </li>
                <li
                    ref={(el) => (listItemRefs.current[3] = el)}
                    className="items-center mb-5 p-5  bg-[rgba(0,0,0,0.45)] backdrop-blur border-black rounded-xl shadow-lg animate-fade-in"
                >
                    {/* Rest of the code */}
                </li>
            </ul>
        </div>
    );
}

export default Skills;
