import React, { useState } from 'react';
import { Link } from 'react-scroll';

const Header = () => {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);

    const toggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);
    };

    const handleLinkClick = () => {
        setIsDropdownOpen(false);
    };

    return (
        <header className="fixed top-0 left-0 w-full z-50 bg-white shadow-md absolute inset-0 bg-[rgba(255,255,255,0.5)] backdrop-filter backdrop-blur">
            <nav className="flex items-center justify-between px-6 py-4">
                <div className="flex items-center">
                    <Link
                        to="home"
                        smooth={true}
                        duration={1000}
                        className="text-xl font-bold text-gray-800 cursor-pointer"
                        onClick={handleLinkClick}
                    >
                        Home
                    </Link>
                </div>
                <div className="flex items-center">
                    <div className="hidden md:flex">
                        <Link
                            to="about"
                            smooth={true}
                            duration={1000}
                            offset={-100}
                            className="ml-4 text-gray-600 hover:text-gray-900 transition duration-300 ease-in-out cursor-pointer"
                            onClick={handleLinkClick}
                        >
                            About
                        </Link>
                        <Link
                            to="services"
                            smooth={true}
                            duration={1000}
                            offset={-100}
                            className="ml-4 text-gray-600 hover:text-gray-900 transition duration-300 ease-in-out cursor-pointer"
                            onClick={handleLinkClick}
                        >
                            Services
                        </Link>
                        <Link
                            to="contact"
                            smooth={true}
                            duration={1000}
                            offset={-100}
                            className="ml-4 text-gray-600 hover:text-gray-900 transition duration-300 ease-in-out cursor-pointer"
                            onClick={handleLinkClick}
                        >
                            Contact Me
                        </Link>
                    </div>
                    <div className="md:hidden">
                        <button
                            className="focus:outline-none text-gray-600 hover:text-gray-900 transition duration-300 ease-in-out"
                            onClick={toggleDropdown}
                        >
                            <svg
                                className="w-6 h-6"
                                fill="none"
                                stroke="currentColor"
                                viewBox="0 0 24 24"
                                xmlns="http://www.w3.org/2000/svg"
                            >
                                {isDropdownOpen ? (
                                    <path
                                        strokeLinecap="round"
                                        strokeLinejoin="round"
                                        strokeWidth={2}
                                        d="M6 18L18 6M6 6l12 12"
                                    />
                                ) : (
                                    <path
                                        strokeLinecap="round"
                                        strokeLinejoin="round"
                                        strokeWidth={2}
                                        d="M4 6h16M4 12h16m-7 6h7"
                                    />
                                )}
                            </svg>
                        </button>
                    </div>
                </div>
            </nav>
            {isDropdownOpen && (
                <div className="md:hidden bg-white shadow-md">
                    <Link
                        to="about"
                        smooth={true}
                        duration={1000}
                        offset={-100}
                        className="block py-2 px-4 text-gray-600 hover:text-gray-900 transition duration-300 ease-in-out cursor-pointer"
                        onClick={handleLinkClick}
                    >
                        About
                    </Link>
                    <Link
                        to="services"
                        smooth={true}
                        duration={1000}
                        offset={-100}
                        className="block py-2 px-4 text-gray-600 hover:text-gray-900 transition duration-300 ease-in-out cursor-pointer"
                        onClick={handleLinkClick}
                    >
                        Services
                    </Link>
                    <Link
                        to="contact"
                        smooth={true}
                        duration={1000}
                        offset={-100}
                        className="block py-2 px-4 text-gray-600 hover:text-gray-900 transition duration-300 ease-in-out cursor-pointer"
                        onClick={handleLinkClick}
                    >
                        Contact Me
                    </Link>
                </div>
            )}
        </header>
    );
};

export default Header;
