import About from './Pages/About.js';
import Services from './Pages/Services.js';
import Contact from './Pages/Contact.js'
import React from 'react';
import { BrowserRouter } from "react-router-dom";
import ResumeDataProvider from './Providers/ResumeDataProvider.js';
import Home from './Pages/Home.js'
import Footer from './components/Footer.js';
import NavBar from './components/NavBar.js';

import './styles/style.css';

function App() {

  let items = document.querySelectorAll(".threeDContent");

  items.forEach(item => {
    item.addEventListener('mousemove', (e) => {
      let positionPx = e.x - item.getBoundingClientRect().left;
      let positionX = (positionPx / item.offsetWidth) * 100;
      let positionPy = e.y - item.getBoundingClientRect().top;
      let positionY = (positionPy / item.offsetHeight) * 100;

      item.style.setProperty('--rX', (0.2) * (positionY) + 'deg');
      item.style.setProperty('--rY', -(0.2) * (positionX) + 'deg');
    })
    item.addEventListener("mouseout", () => {
      item.style.setProperty('--rX', '0deg');
      item.style.setProperty('--rY', '0deg');
    })
  })

  let cursor = document.getElementById("cursor");
  let page = document.getElementById("main");
  //page.addEventListener("mousemove", (e) => {
    cursor.style.position = "absolute";
    cursor.style.left = e.x + 'px';
    cursor.style.top = e.y + 'px';
 // })

  return (
    <div id="main" className=" overflow-hidden">
      <svg
        viewBox="0 0 88 88"
        className="text-gray-800 absolute"
        id='cursor'
      >
        <circle
          fill="currentColor"
          fillOpacity="0.4"
          cx="0"
          cy="0"
          r="2"
        />
      </svg>

      <ResumeDataProvider>
        <div id="top"></div>
        <NavBar />
        <Home />
        <BrowserRouter>
          <About />
        </BrowserRouter>
        <Services />
        <Contact />
        <Footer />
      </ResumeDataProvider>
    </div>
  );
}

export default App;
