import React, { useEffect } from "react";
import { useResumeDataContext } from "../Providers/ResumeDataProvider";
import Typed from 'react-typed';

const TypingText = ({ text }) => (
    <Typed
        strings={[text]}
        typeSpeed={150}
        backSpeed={200}
        loop={true}
    />
);

const Home = () => {
    const { resumeData } = useResumeDataContext();

    useEffect(() => {
        const circles = document.querySelectorAll(".circle-animation");

        circles.forEach((circle, index) => {
            const speed = (index);
            const direction = index % 2 === 0 ? -1 : 1;

            circle.animate(
                [
                    { transform: "translateX(0px) translateY(0px)" },
                    { transform: `translateX(${direction * 200}px) translateY(${direction * 100}px)` },
                ],
                {
                    duration: 2000 / speed,
                    iterations: Infinity,
                    direction: "alternate",
                }
            );
        });
    }, []);

    return (
        <div id="home" className="h-screen flex justify-center items-center p-10 fixed top-0 inset-x-0 z-0">
            <h1 className="text-4xl font-mono z-10">
                {"<h1> Hi I'm <span>"}
                <span className="text-red-800">
                    <TypingText text={resumeData[0].name} />
                </span>
                {"</span> </h1>"}
            </h1>
            <div className="absolute inset-x-0 top-0 items-center justify-center overflow-hidden flex inset-y-0">
                <svg
                    viewBox="0 0 88 88"
                    className="w-full max-w-screen-xl text-[rgb(9,66,255)]"
                >
                    <circle
                        fill="currentColor"
                        fillOpacity="0.4"
                        cx="44"
                        cy="44"
                        r="15.5"
                        className="circle-animation"
                    />
                    <circle
                        fillOpacity="0.1"
                        fill="currentColor"
                        cx="44"
                        cy="44"
                        r="44"
                        className="circle-animation"
                    />
                    <circle
                        fillOpacity="0.1"
                        fill="currentColor"
                        cx="44"
                        cy="44"
                        r="37.5"
                        className="circle-animation"
                    />
                    <circle
                        fillOpacity="0.1"
                        fill="currentColor"
                        cx="44"
                        cy="44"
                        r="29.5"
                        className="circle-animation"
                    />
                    <circle
                        fillOpacity="0.1"
                        fill="currentColor"
                        cx="44"
                        cy="44"
                        r="22.5"
                        className="circle-animation"
                    />
                </svg>
            </div>
        </div>
    )
}

export default Home;
