import React, { useEffect, useRef, useState } from 'react';
import { Routes, Route, Link } from "react-router-dom";
import Skills from "../components/Skills";
import Education from '../components/Education';
import Experience from '../components/Experience';
import { useResumeDataContext } from "../Providers/ResumeDataProvider";
import UserImg from "../Assets/images/meetPic.jpg"
import '../styles/style.css';

const About = () => {
    const { resumeData } = useResumeDataContext();
    return (
        <div id="about" className="relative mx-auto sm:max-w-2xl md:max-w-full lg:max-w-screen-2xl md:px-26 lg:px-8 mt-screen z-10 bg-[rgba(46,46,46,0.69)] backdrop-blur border-black rounded-t-3xl text-white">
            <div className="flex flex-col lg:flex-row p-10 threeDWrap">
                <div className='flex justify-center p-6 rounded-3xl'>
                    <img src={UserImg} alt="Meet Suthar" className="max-h-96 max-w-fit rounded-xl shadow-2xl transition-transform duration-500 ease-in-out transform hover:scale-105" />
                </div>

                <div id="aboutInfo" className=" threeDContent about-info threeD rounded-2xl shadow-2xl mt-8 lg:mt-0 lg:ml-8 p-8 max-w-5xl backdrop-blur border-black bg-[rgba(0,0,0,0.31)] hover:rota">
                    <div
                        id="aboutInfoTitle"
                        className="opacity-0 transform transition-opacity duration-500 delay-500 ease-in-out"
                    >
                        <h1 className="text-4xl pb-4">About Me</h1>
                        <p className='pb-4 text-lg'>{resumeData[0].aboutMe}</p>
                    </div>
                    <hr className="w-full my-6 border-gray-300" />
                    <div>
                        <nav id="AboutTabTitles" className="flex justify-center mt-4 text-center">
                            <Link to="/" className=" text-lg text-white transition-all duration-200 ease-in-out hover:scale-110 bg-[rgba(255,255,255,0.21)] pr-3 pl-3 mr-1 ml-1 rounded-xl focus:bg-[rgba(139,139,139,0.21)] hover:shadow-2xl w-28 pt-2">Skills</Link>
                            <Link to="/experience" className="text-lg text-white transition-all duration-200 ease-in-out hover:scale-110 bg-[rgba(255,255,255,0.21)] pr-3 pl-3 mr-1 ml-1 rounded-xl focus:bg-[rgba(139,139,139,0.21)] hover:shadow-2xl w-28 pt-2">Experience</Link>
                            <Link to="/education" className="text-lg text-white transition-all duration-200 ease-in-out hover:scale-110 bg-[rgba(255,255,255,0.21)] pr-3 pl-3 mr-1 ml-1 rounded-xl focus:bg-[rgba(139,139,139,0.21)] hover:shadow-2xl w-28 h-12 pt-2">Education</Link>
                        </nav>
                    </div>
                    <div
                        id="AboutTabs"
                        className="mt-8 opacity-0 transform transition-opacity duration-500 delay-500 ease-in-out"

                    >
                        <Routes>
                            <Route path="/" element={<Skills />} />
                            <Route path="/experience" element={<Experience />} />
                            <Route path="/education" element={<Education />} />
                        </Routes>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default About;
