import React from "react";
import { useResumeDataContext } from "../Providers/ResumeDataProvider";
import { FaGraduationCap, FaCertificate } from 'react-icons/fa';

const Education = () => {
    const { resumeData } = useResumeDataContext();

    return (
        <div className="tab-contents active-tab">
            {resumeData[0].education.map((education, index) => (
                <div key={index} className="bg-opacity-90 mb-5 p-5 bg-[rgba(46,46,46,0.77)] backdrop-blur border-black rounded-3xl shadow-lg">
                    <div className='flex items-center'>
                        <FaGraduationCap className="text-3xl mr-4 mt-1"/>
                        <h1 className="text-xl font-semibold">{education.degree}</h1>
                    </div>
                    <div>
                        <p className="text-lg mb-2">{education.university}</p>
                        <p className="mb-1">Completion Date: {education.completionDate}</p>
                        <p className="mb-1">Grade: {education.grade}</p>
                    </div>
                </div>
            ))}
            <div className="items-center mb-5 p-5 bg-[rgba(46,46,46,0.77)] backdrop-blur border-black rounded-3xl shadow-lg">
                <FaCertificate className="text-2xl mr-4 mt-1" />
                <div>
                    <h3 className="text-xl font-semibold mb-2">Certifications:</h3>
                    <ul className="mb-2">
                        {Object.values(resumeData[0].certifications[0]).map(
                            (certification, index) => (
                                <li key={index} className="mb-1">
                                    {certification[0].name} | Completion Date: {certification[0].completionDate}
                                </li>
                            )
                        )}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default Education;
