import React, { useState } from 'react';

const Header = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLinkClick = (sectionId) => {
    const element = document.getElementById(sectionId);
    element.scrollIntoView({ behavior: 'smooth' });
    setIsDropdownOpen(false);
  };

  return (
    <header className="fixed top-0 left-0 w-full z-50 bg-white shadow-md">
      <nav className="flex items-center justify-between px-6 py-4">
        <div className="flex items-center">
          <a href="#home" className="text-xl font-bold text-gray-800">
            Home
          </a>
          <div className="ml-4 hidden md:block">
            <a
              href="#about"
              className="ml-4 text-gray-600 hover:text-gray-800 transition duration-300 ease-in-out"
            >
              About
            </a>
            <a
              href="#services"
              className="ml-4 text-gray-600 hover:text-gray-800 transition duration-300 ease-in-out"
            >
              Services
            </a>
            <a
              href="#contact"
              className="ml-4 text-gray-600 hover:text-gray-800 transition duration-300 ease-in-out"
            >
              Contact Me
            </a>
          </div>
        </div>
        <div className="md:hidden">
          <button
            className="focus:outline-none text-gray-600 hover:text-gray-800 transition duration-300 ease-in-out"
            onClick={toggleDropdown}
          >
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              {isDropdownOpen ? (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M6 18L18 6M6 6l12 12"
                />
              ) : (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M4 6h16M4 12h16m-7 6h7"
                />
              )}
            </svg>
          </button>
        </div>
      </nav>
      {isDropdownOpen && (
        <div className="md:hidden bg-white">
          <a
            href="#about"
            className="block py-2 px-4 text-gray-600 hover:bg-gray-100 transition duration-300 ease-in-out"
            onClick={() => handleLinkClick('about')}
          >
            About
          </a>
          <a
            href="#services"
            className="block py-2 px-4 text-gray-600 hover:bg-gray-100 transition duration-300 ease-in-out"
            onClick={() => handleLinkClick('services')}
          >
            Services
          </a>
          <a
            href="#contact"
            className="block py-2 px-4 text-gray-600 hover:bg-gray-100 transition duration-300 ease-in-out"
            onClick={() => handleLinkClick('contact')}
          >
            Contact Me
          </a>
        </div>
      )}
    </header>
  );
};

export default Header;
